<template>
	<view>
		<view class="default" v-if="isIframe && !couponList.length">
			<text>优惠券，暂无数据</text>
		</view>
		<view class="coupon" v-if="couponList.length&&isShow&&!isIframe">
			<view class="title acea-row row-between-wrapper">
				<view class="acea-row row-middle">
					<view class="sign">
						<image src="../../../static/images/sign01.png"></image>
					</view>
					<view class="name">领专享<text>优惠券</text></view>
				</view>
				<navigator url="/pages/users/user_get_coupon/index" hover-class="none" class="more acea-row row-center-wrapper">更多<text
					 class="iconfont icon-xiangyou"></text></navigator>
			</view>
			<view class="conter">
				<scroll-view scroll-x="true" style="white-space: nowrap; vertical-align: middle;" show-scrollbar="false">
					<view class="itemCon" :class="item.is_use?'on':''" v-for="(item, index) in couponList" :key="index">
						<view class="item acea-row row-between-wrapper">
							<view class="text">
								<view class="money line1">¥<text class="num">{{item.coupon_price}}</text></view>
								<view class="line1">满{{item.use_min_price}}可用</view>
							</view>
							<view class="bnt" v-if="item.is_use===true"><text>已领取</text></view>
							<view class="bnt" v-else-if="item.is_use===false" @click="receiveCoupon(item)"><text>领取</text></view>
							<view class="bnt" v-else-if="item.is_use===2"><text>已过期</text></view>
						</view>
					</view>
				</scroll-view>
			</view>
		</view>
		<view class="coupon" v-if="couponList.length && isIframe">
			<view class="title acea-row row-between-wrapper">
				<view class="acea-row row-middle">
					<view class="sign">
						<image src="../../../static/images/sign01.png"></image>
					</view>
					<view class="name">领专享<text>优惠券</text></view>
				</view>
				<navigator url="/pages/users/user_get_coupon/index" hover-class="none" class="more acea-row row-center-wrapper">更多<text
					 class="iconfont icon-xiangyou"></text></navigator>
			</view>
			<view class="conter">
				<scroll-view scroll-x="true" style="white-space: nowrap; vertical-align: middle;" show-scrollbar="false">
					<view class="itemCon" :class="item.is_use?'on':''" v-for="(item, index) in couponList" :key="index">
						<view class="item acea-row row-between-wrapper">
							<view class="text">
								<view class="money line1">¥<text class="num">{{item.coupon_price}}</text></view>
								<view class="line1">满{{item.use_min_price}}可用</view>
							</view>
							<view class="bnt" v-if="item.is_use===true"><text>已领取</text></view>
							<view class="bnt" v-else-if="item.is_use===false"><text>领取</text></view>
							<view class="bnt" v-else-if="item.is_use===2"><text>已过期</text></view>
						</view>
					</view>
				</scroll-view>
			</view>
		</view>
	</view>
</template>

<script>
	let app = getApp();
	import {
		getIndexCoupons,
		setCouponReceive
	} from '@/api/api.js';
	import {
		toLogin
	} from '@/libs/login.js';
	import { mapGetters } from 'vuex';
	export default {
		name: 'coupon',
		props: {
			dataConfig: {
				type: Object,
				default: () => {}
			}
		},
		computed: {
			 ...mapGetters(['isLogin'])
		},
		watch: {
			dataConfig: {
				immediate: true,
				handler(nVal, oVal) {
					if(nVal){
						this.numberConfig = nVal.numConfig.val;
						this.isShow = nVal.isShow.val;
						this.getCoupon();
					}
				}
			}
		},
		data() {
			return {
				couponList: [],
				numberConfig:0,
				name:this.$options.name,//component组件固定写法获取当前name；
				isIframe:app.globalData.isIframe,//判断是前台还是后台；
				isShow:true//判断此模块是否显示；
			};
		},
		created() {},
		mounted() {},
		methods: {
			getCoupon: function() {
				let that = this;
				let lists = [];
				getIndexCoupons({type:-1,num:this.numberConfig}).then(res => {
					res.data.forEach(function(value, key, iterable) {
					  if(!value.used){
						 lists.push(value);
					  }
					});
					that.$set(that, 'couponList', lists);
				}).catch(err => {
					return that.$util.Tips({
						title: err
					});
				});
			},
			receiveCoupon: function(item) {
				let that = this;
				if (!that.isLogin) {
					toLogin();
				} else {
					setCouponReceive(item.id)
						.then(function() {
							item.is_use = true;
							that.$set(that, 'couponList', that.couponList);
							that.$util.Tips({
								title: "领取成功"
							});
						})
						.catch(function(err) {
							that.$util.Tips({
								title: err
							});
						});
				}
			}
		}
	}
</script>

